/* Coded By alireza mhm @myFrontCodes */var styleSheet = `<style>@import url('https://fonts.googleapis.com/css2?family=Montserrat&display=swap');.sizeinpage-container { width: 180px; height: 80px; border-radius: 15px; display: flex; justify-content: space-around; align-items: center; box-sizing: border-box; padding: 10px; font-family: 'Montserrat', sans-serif; font-weight: bold; position: fixed; z-index: 100000000000000000;}.sizeinpage-dark { background-color: #252629; --color: rgba(255, 255, 255, .9); --circle-color: rgba(255, 255, 255, .4);}.sizeinpage-light { background-color: #fff; --color: #252629; --circle-color: rgba(0, 0, 0, .1); box-shadow: 0px 0px 10px -5px #000 inset;}.sizeinpage-showsize-container { display: flex; flex-direction: column; align-items: center;}.sizeinpage-breakpoint-container { display: flex; justify-content: center; align-items: center;}.sizeinpage-breakpoint-circle { width: 50px; height: 50px; border-radius: 50%; display: flex; justify-content: center; align-items: center; background: var(--circle-color)}.sizeinpage-size { margin: 3px; font-size: .9rem; color: var(--color);}#breakpoint { color: var(--color);}#sizeinpage-close { stroke: var(--color); position: absolute; top: 1px; right: 1px; padding: 5px; cursor: pointer; transition: opacity .2s;}#sizeinpage-close:hover { opacity: .8;}</style>`;var htmlSource = `<div class="sizeinpage-container sizeinpage-dark"><div id="sizeinpage-close"> <svg xmlns="http://www.w3.org/2000/svg" width="15" height="15" viewBox="0 0 24 24" fill="none" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="feather feather-x"><line x1="18" y1="6" x2="6" y2="18"></line><line x1="6" y1="6" x2="18" y2="18"></line></svg></div><div class="sizeinpage-showsize-container"> <span class="sizeinpage-size" title="width"> <span>W :</span> <span id="sizeinpage-width">null</span>px</span> <span class="sizeinpage-size" title="height"> <span>H :</span> <span id="sizeinpage-height">null</span>px</span></div><div class="sizeinpage-breakpoint-container"> <div class="sizeinpage-breakpoint-circle"> <span id="breakpoint">null</span> </div></div></div>`;document.head.innerHTML += styleSheet;document.body.innerHTML += htmlSource;var container = document.getElementsByClassName("sizeinpage-container")[0];function startSizeInPage(setting) { var theme, align, opacity, help; if (setting) { if (setting.theme) { theme = setting.theme; } else { theme = "dark"; } if (setting.align) { align = setting.align; } else { align = "bl"; } if (setting.opacity) { opacity = setting.opacity; } else { opacity = 1.0; } if (setting.help) { if (setting.help == "en") { sizeinpageHelp("en"); } else if (setting.help == "fa") { sizeinpageHelp("fa"); } }; }; switch (theme) { case "dark": { sizeinpageDark(); } break; case "light": { sizeinpageLight(); } break; default: { sizeinpageDark(); } break; } switch (align) { case "tl": { container.style.top = "1%"; container.style.left = ".5%"; } break; case "tr": { container.style.top = "1%"; container.style.right = ".5%"; } break; case "bl": { container.style.bottom = "1%"; container.style.left = ".5%"; } break; case "br": { container.style.bottom = "1%"; container.style.right = ".5%"; } break; case "center": { container.style.top = "50%"; container.style.left = "50%"; container.style.transform = "translate(-50%,-50%)" } break; default: container.style.bottom = "1%"; container.style.left = ".5%"; break; } container.style.opacity = opacity; function sizeinpageDark() { container.classList.remove("sizeinpage-light"); container.classList.add("sizeinpage-dark"); }; function sizeinpageLight() { container.classList.remove("sizeinpage-dark"); container.classList.add("sizeinpage-light"); }; getPageSize(); window.addEventListener("resize", getPageSize); document.getElementById("sizeinpage-close").addEventListener("click", () => { container.style.display = "none"; }) ;function getPageSize() { let breakPoint = "XS"; let page_width = window.innerWidth || document.documentElement.innerWidth || document.body.innerWidth; let page_height = window.innerHeight || document.documentElement.innerHeight || document.body.innerHeight; document.getElementById("sizeinpage-width").innerHTML = page_width; document.getElementById("sizeinpage-height").innerHTML = page_height; if (page_width < 576) { breakPoint = "xs"; } if (page_width >= 576) { breakPoint = "sm"; } if (page_width >= 768) { breakPoint = "md"; } if (page_width >= 992) { breakPoint = "lg"; } if (page_width >= 1200) { breakPoint = "xl"; } if (page_width >= 1400) { breakPoint = "xxl"; }; document.getElementById("breakpoint").innerHTML = breakPoint; } function sizeinpageHelp(lang) { if (lang == "en") { console.log("%c sizeInPage ", "color:yellow;font-size:1rem;", "\n", "Is a JavaScript plugin that helps you better design websites"); console.group("%c How", "font-size:1.2rem;"); console.log("Used only for sizing and displays width, height and breakpoint \nso you can work on sizing better"); console.groupEnd(); console.groupCollapsed("%c How to use", "font-size:1.2rem;"); console.log("Just link the sizeInPage.js file to your page and then call the startSizeInPage function \n- <script src=\"sizeInPage.js\"></script> \n- startSizeInPage()"); console.groupEnd(); console.groupCollapsed("%c Options", "font-size:1.2rem;"); console.log("+ theme(Set General theme) \n+ align(Set the location on the page) \n+ opacity(Adjust transparency) \n+ help(Show help)"); console.log("Use options :\nTo use the options, we must pass them as an object and \nas an argument to the startSizeInPage() function."); console.log("example :\nstartSizeInPage({\n theme: 'dark',\n align: 'bl',\n opacity: '1',\n help:'en'\n})"); console.groupEnd(); console.groupCollapsed("%c Options values", "font-size:1.2rem;"); console.groupCollapsed("%c theme", "font-size:1rem"); console.log("theme: 'dark' | 'theme': light\n(theme: dark is enabled by default)\nExample of use:\nstartSizeInPage({\n theme: 'dark'\n})\nor\nstartSizeInPage({\n theme: 'light'\n})"); console.groupEnd(); console.groupCollapsed("%c align", "font-size:1rem;"); console.log("align : 'tl' | 'tr' | 'bl' | 'br' | 'center' \n(align: 'bl' is enabled by default)\n(Values ​​stand for directions, example: tr = top right)\nExample of use:\nstartSizeInPage({\n align: 'tl' (example)\n})"); console.groupEnd(); console.groupCollapsed("%c opacity", "font-size:1rem"); console.log("To set the opacity and its value is between 0 and 1 \n(opacity: 1 is enabled by default)\nExample of use:\nstartSizeInPage({\n opacity: '0.8' (example)\n})"); console.groupEnd(); console.groupCollapsed("%c help", "font-size:1rem"); console.log("help : 'en' | 'fa' \nTo display the plugin guide (en = in English , fa = in Persian) \n(help: 'none' is enabled by default)\nExample of use:\nstartSizeInPage({\n help: 'en' (example)\n})"); console.groupEnd(); console.groupEnd(); console.groupEnd(); console.log("%ccoded by alireza mhm (@myFrontCodes)\n\nhttps://github.com/wwwAlireza\nhttps://codepen.io/alireza82", "color: green;font-size:1rem"); } if (lang == "fa") { console.log("بدلیل محدودیت ها امکان نمایش راهنما به زبان فارسی نیست"); }; }}